    .byte 0x04              ; Func_Mode             (4)
    .byte 0x10              ; SWSearchRange         (16)
    .byte 0x40              ; SWB_Width             (64)
    .byte 0x10              ; SW_Start_Offset       (16)
    .word 0x00000000        ; MVP                   (0,0)
    .half 0x3a98            ; Skip_SAD_Thrld        (15000)
    .half 0x3a98            ; Inter_SAD_Thrld       (15000)
    .half 256               ; SWBaseAddr
